     $html .= "  { \n";        $html .= "    try\n";        $html .= "    {\n";        $html .= "      e = xGetElementById('Contenedor');\n";        $html .= "      e.style.display = \"none\";\n";        $html .= "    }\n";        $html .= "    catch(error){}\n";        $html .= "  }\n";        $html .= "  function MostrarSpan()\n";        $html .= "  { \n";        $html .= "    try\n";        $html .= "    {\n";        $html .= "      e = xGetElementById('Contenedor');\n";        $html .= "      e.style.display = \"\";\n";        $html .= "      Iniciar();\n";        $html .= "    }\n";        $html .= "    catch(error){alert(error)}\n";        $html .= "  }\n";        $html .= "  function MostrarTitle(Seccion)\n";        $html .= "  {\n";        $html .= "    xShow(Seccion);\n";        $html .= "  }\n";        $html .= "  function OcultarTitle(Seccion)\n";        $html .= "  {\n";        $html .= "    xHide(Seccion);\n";        $html .= "  }\n";        $html .= "  function Iniciar()\n";        $html .= "  {\n";        $html .= "    contenedor = 'Contenedor';\n";        $html .= "    titulo = 'titulo';\n";        $html .= "    ele = xGetElementById('Contenido');\n";        $html .= "    xResizeTo(ele," . $tmn . ", 'auto');\n";        $html .= "    ele = xGetElementById(contenedor);\n";        $html .= "    xResizeTo(ele," . $tmn . ", 'auto');\n";        $html .= "    xMoveTo(ele, xClientWidth()/4, xScrollTop()+20);\n";        $html .= "    ele = xGetElementById(titulo);\n";        $html .= "    xResizeTo(ele," . ($tmn - 20) . ", 20);\n";        $html .= "    xMoveTo(ele, 0, 0);\n";        $html .= "    xEnableDrag(ele, myOnDragStart, myOnDrag, myOnDragEnd);\n";        $html .= "    ele = xGetElementById('cerrar');\n";        $html .= "    xResizeTo(ele,20, 20);\n";        $html .= "    xMoveTo(ele," . ($tmn - 20) . ", 0);\n";        $html .= "  }\n";        $html .= "  function myOnDragStart(ele, mx, my)\n";        $html .= "  {\n";        $html .= "    window.status = '';\n";        $html .= "    if (ele.id == titulo) xZIndex(contenedor, hiZ++);\n";        $html .= "    else xZIndex(ele, hiZ++);\n";        $html .= "    ele.myTotalMX = 0;\n";        $html .= "    ele.myTotalMY = 0;\n";        $html .= "  }\n";        $html .= "  function myOnDrag(ele, mdx, mdy)\n";        $html .= "  {\n";        $html .= "    if (ele.id == titulo) {\n";        $html .= "      xMoveTo(contenedor, xLeft(contenedor) + mdx, xTop(contenedor) + mdy);\n";        $html .= "    }\n";        $html .= "    else {\n";        $html .= "      xMoveTo(ele, xLeft(ele) + mdx, xTop(ele) + mdy);\n";        $html .= "    }  \n";        $html .= "    ele.myTotalMX += mdx;\n";        $html .= "    ele.myTotalMY += mdy;\n";        $html .= "  }\n";        $html .= "  function myOnDragEnd(ele, mx, my)\n";        $html .= "  {\n";        $html .= "  }\n";        $html .= "</script>\n";        $html .= "<div id='Contenedor' class='d2Container' style=\"display:none;z-index:4\">\n";        $html .= "  <div id='titulo' class='draggable' style=\" text-transform: uppercase;text-align:center;\">" . $Nombre . "</div>\n";        $html .= "  <div id='cerrar' class='draggable'><a class=\"hcPaciente\" href=\"javascript:OcultarSpan()\" title=\"Cerrar\" style=\"font-size:9px;\">X</a></div><br><br>\n";        $html .= "  <div id='Contenido' class='d2Content'>\n";        $html .= "  </div>\n";        $html .= "</div>\n";        return $html;    }    /**     * Funcion donde se crea la Forma del Contrato a llenar       * @param array $datos vector que contiene la informacion de Los Proveedores     * @param array $action vector que contiene los link de la aplicacion     * @param array $datos_empresa vector que contiene la informacion de Empresa que contrata al proveedor     * @param string $fecha contiene la informacion de la fecha actual.     * @return string $html retorna la cadena con el codigo htm