<?php  /**  * @package IPSOFT-SIIS  * @version $Id: Servicios.class.php,v 1.1 2009/12/14 15:05:34 hugo Exp $Revision: 1.1 $  * @copyright (C) 2005 IPSOFT - SA (www.ipsoft-sa.com)  * @author Hugo F. Manrique  */  class Servicios extends ConexionBD  {    /**    * Constructor de la clase    */    function Servicios(){}    /**    * Funcion donde se obtienen los servicios    *    * @return mixed    */    function ObtenerServicios()    {      $sql  = "SELECT servicio,";      $sql .= " 	    descripcion, ";      $sql .= " 	    sw_fecha_incapacidad ";      $sql .= "FROM   servicios ";      $sql .= "ORDER BY servicio ";            if(!$rst = $this->ConexionBaseDatos($sql))        return false;      $datos = array();      while(!$rst->EOF)      {        $datos[] =  $rst->GetRowAssoc($ToUpper = false);        $rst->MoveNext();      }      $rst->Close();            return $datos;    }    /**    * Funcion donde se obtienen los servicios    *    * @return mixed    */    function ActualizarIncapacidadServicios($estado,$servicio)    {      $sql  = "UPDATE servicios  ";      $sql .= "SET    sw_fecha_incapacidad = '".$estado."' ";      $sql .= "WHERE  servicio = '".$servicio."' ";            if(!$rst = $this->ConexionBaseDatos($sql))        return false;            return true;    }  }?>